changeSet.sql:
CREATE TABLE public.Emblem ( id integer PRIMARY KEY NOT NULL, name text );
CREATE TABLE public.Country ( id integer PRIMARY KEY NOT NULL, short_name text NOT NULL, full_name text NOT NULL, emblem_id integer REFERENCES Emblem.id );
CREATE TABLE public.Nationality ( id SERIAL NOT NULL, name text NOT NULL, skin_color text );
CREATE TABLE public.Region ( id SERIAL PRIMARY KEY NOT NULL name text NOT NULL, code integer, population_amount integer, geo_data varchar, emblem_id integer REFERENCES Emblem.id );
CREATE TABLE public.Locality_type ( id SERIAL PRIMARY KEY NOT NULL, type text, description text );
CREATE TABLE public.Locality ( id SERIAL PRIMARY KEY NOT NULL, name text NOT NULL, population_amount integer, is_capital text, is_region_centre text, country_id integer REFERENCES Country.id, locality_type_id integer REFERENCES Locality_type.id, emblem_id integer REFERENCES Emblem.id, region_id integer REFERENCES Region.id );
CREATE TABLE public.Country_nationalities ( country_id integer REFERENCES Country.id, nation_id integer REFERENCES Nationality.id );
